import { Icon } from './Icon';

import { iconList } from './../../../../utilities/icons';

console.log("icon.storis", iconList)
export default {
  title: 'Example/Atoms/Icon',
  component: Icon,
  argTypes: {
    iconName: {
      control: {
        type: 'select', // Usa il tipo select
        options: iconList, // Opzioni dinamiche
      },
      description: 'Nome dell\'icona (senza prefisso "icon-")',
    },
    color: {
      control: 'color',
      description: 'Colore dell\'icona',
    },
    size: {
      options: ['x-small', 'small', 'medium', 'large'],
      control: { type: 'radio' },
    },
  },
};

//const Template = (args) => <Icon {...args} />;


export const XSmall = {
  args: {
    iconName: 'alarm', 
    color: '#000', 
    size: 'x-small'
  }
}

export const Small = Template.bind({});
XSmall.args = {
  size: 'small'
};

export const Medium = Template.bind({});
XSmall.args = {
  size: 'medium'
};

export const Large = Template.bind({});
XSmall.args = {
  size: 'large'
};
