import React from 'react';
import PropTypes from 'prop-types';
import { iconList } from './../../../../utilities/icons'
//import icomoonSelection from './../../../../font/icons/selection.json'
import './icon.css';

//const iconList = icomoonSelection.icons.map((icon) => icon.properties.name);


console.log("iconLisssst", iconList)



export const Icon = ({ iconName, color, size}) => {
  return (
    <i 
      className={[`icon-${iconName}`, `icon-${size}`]} 
      style={{ color }} 
      aria-hidden="true"
    ></i>
  );
};

Icon.propTypes = {
  iconName: PropTypes.oneOf(iconList).isRequired, // Nome dell'icona (senza "icon-")
  color: PropTypes.string, // Colore dell'icona
  size: PropTypes.oneOf(['x-small','small', 'medium', 'large'])
};

Icon.defaultProps = {
  color: '#000', // Default: Nero
  size: 'small'
};
