import React from 'react';
import PropTypes from 'prop-types';

import './../../../../font/icons/style.css';
import './skl-ds-card.scss';

import { Text } from '../../atoms/skl-ds-text/skl-ds-text';
import { Widget } from '../../molecules/skl-ds-widget/skl-ds-widget';

export const Card = ({ icon, iconSize, orientation, text, colorText }) => {

  const fontSize = iconSize === 'small' ? 'small' : iconSize === 'large' ? 'medium' : 'default';
  const orientationClass = orientation === 'horizontal' ? 'horizontal' : 'vertical';

  return (
    <div className={`skl-ds-card skl-ds-widget--${orientationClass}`}>
      <Text tag={'p'} children={text} fontSize={fontSize} color={colorText}/>
    </div>
  );
};

Widget.propTypes = {
  icon: PropTypes.oneOf(['alarm', 'chat', 'check', 'child_care', 'close', 'favorite', 'handyman']).isRequired,
  orientation: PropTypes.oneOf(['horizontal', 'vertical']),
  iconSize: PropTypes.oneOf(['small','large']),
  text: PropTypes.node.isRequired,
  colorText: PropTypes.oneOf(['white', 'black']),
};

Widget.defaultProps = {
  icon: 'alarm',
  iconSize: 'small',
  orientation: 'horizontal',
  text: 'Default Text',
  colorText: 'black'
};
