import { Icon } from './skl-ds-widget';

import { iconList } from '../../../../utilities/icons';

export default {
  title: 'Example/Atoms/skl-ds-icon',
  component: Icon,
  argTypes: {
    iconName: {
      control: {
        type: 'select', 
        options: ['alarm', 'chat', 'check', 'child_care', 'close', 'favorite', 'handyman']
      },
      description: 'Nome dell\'icona (senza prefisso "icon-")',
    },
    color: {
      control: 'color',
      description: 'Colore dell\'icona',
    },
    size: {
      control: { 
        type: 'radio',
        options: ['small', 'medium', 'large', 'xlarge'], 
      },
      description: 'Dimensione dell\'icona',
    },
  },
};


export const Small = {
  args: {
    iconName: 'alarm', 
    color: '#000', 
    size: 'small'
  }
}


export const Medium = {
  args: {
    iconName: 'alarm', 
    color: '#000', 
    size: 'medium'
  }
}

export const Large = {
  args: {
    iconName: 'alarm', 
    color: '#000', 
    size: 'large'
  }
}

export const XLarge = {
  args: {
    iconName: 'alarm', 
    color: '#000', 
    size: 'xlarge'
  }
}
