import { Divider } from './skl-ds-divider';


export default {
  title: 'Example/Atoms/skl-ds-divider',
  component: Divider,
  argTypes: {
    color: {
      control: 'color',
      description: 'Colore del divider',
    },
    opacity: {
      control: {
        type: 'range', 
        min: 0,
        max: 1,
        step: 0.2,
      },
      description: 'Opacità del divider',
    },
    thickness: {
      control: 'text',
      description: 'Spessore del divider (es. "2px", "0.5rem")',
    }
  },
};


export const Horizontal = {
  args: {
    color: '#ccc',
    opacity: 0.2,
    thickness: '1px'
  }
}
