import { Icon } from './Icon';

import { iconList } from './../../../../utilities/icons';

console.log("icon.storis", iconList)
export default {
  title: 'Example/Atoms/Icon',
  component: Icon,
  argTypes: {
    iconName: {
      control: {
        type: 'select', // Usa il tipo select
        options: iconList, // Opzioni dinamiche
      },
      description: 'Nome dell\'icona (senza prefisso "icon-")',
    },
    color: {
      control: 'color',
      description: 'Colore dell\'icona',
    },
    size: {
      options: ['small', 'medium', 'large', 'x-large'],
      control: { type: 'radio' },
    },
  },
};


export const Small = {
  args: {
    iconName: 'alarm', 
    color: '#000', 
    size: 'small'
  }
}


export const Medium = {
  args: {
    iconName: 'alarm', 
    color: '#000', 
    size: 'medium'
  }
}

export const Large = {
  args: {
    iconName: 'alarm', 
    color: '#000', 
    size: 'large'
  }
}

export const XLarge = {
