import React from 'react';
import PropTypes from 'prop-types';
import './skl-ds-widget.css';

export const Widget = ({ tag, color, size, bold, children })=> {


  return <Tag className={`skl-ds-text--${color} skl-ds-text--${size} ${bold ? 'skl-ds-icon--bold' : ''}`}>{children}</Tag>;
};

Text.propTypes = {
  tag: PropTypes.oneOf(['h1', 'h2', 'p']).isRequired, 
  size: PropTypes.oneOf(['small', 'medium']),
  color: PropTypes.oneOf(['white', 'black']),
  children: PropTypes.node.isRequired,
};

Text.defaultProps = {
  tag: 'p',
  color: 'black',
  size: "small",
  bold: false
};
