import React, { useState}  from 'react';

import PropTypes from 'prop-types';
import './skl-ds-card.scss';

import { Text } from './../../atoms/skl-ds-text/skl-ds-text';
import { Divider } from './../../atoms/skl-ds-divider/skl-ds-divider';
import { Widget } from './../../molecules/skl-ds-widget/skl-ds-widget';

export const Card = ({ state,textHeader, cardTitle}) => {
  const [cardState, setCardState] = useState(state);

  const widgetData = [
    { icon: 'handyman', iconSize: 'small', colorText: 'black' },
    { icon: 'child_care', iconSize: 'small', colorText: 'black'},
    { icon: 'alarm', iconSize: 'small', colorText: 'black', state: cardState},
  ];

  const [widgetStates, setWidgetStates] = useState(widgetData);

  const getTextByState = (state) => {
    switch (state) {
      case 'start':
        return 'Inizio';
      case 'important':
        return 'Attenzione';
      case 'expired':
        return 'expired';
      case 'end':
        return 'end';
      default:
        return 'N/D';
    }
  };




  return <div className={`skl-ds-card skl-ds-card--${cardState}`}>
      <div className='skl-ds-card-header'>        
          <Text 
            tag={'p'} 
            children={textHeader} 
            fontSize={'small'} 
            color={'black'}
          />
      </div>

      <div className='skl-ds-card-body'>
        <Text 
          className="skl-ds-card-body__title"
          tag={'h1'} 
          children={cardTitle} 
          fontSize={'small'} 
          color={'black'}
          bold
        />

        <Divider />

        <div className='skl-ds-card-body__infos'>
          {widgetStates.map((data, index) => (
            <Widget 
              key={index}
              icon={data.icon}
              iconSize={data.iconSize}
              colorText={data.colorText}
              text={getTextByState(data.state)}
            />
          ))}
        </div>

         <Divider />
      </div>      
  </div>
};

Card.propTypes = {
  state: PropTypes.oneOf(['normal', 'important', 'expired', 'done']),
  textHeader: PropTypes.string,
  cardTitle: PropTypes.string
};

Card.defaultProps = {
  state: 'start',
  textHeader: 'Emanuele',
  cardTitle: 'This is a card title and can be at his maximum lenght about three lines. If the content exceed you need to cut it.'
};
