import { Text } from './skl-ds-text';

export default {
  title: 'Example/Atoms/skl-ds-text',
  component: Text,
  argTypes: {
    tag: {
      control: {
        type: 'select', 
        options: ['h1', 'h2', 'p']
      },
      description: 'Tipo di elemento HTML (h1, h2, p)',
    },
    color: {
      control: 'color', // Controllo per scegliere il colore
      description: 'Colore del testo',
    },
    children: {
      control: 'text',
      description: 'Contenuto del testo',
    },
  },
};

const Template = (args) => <Text {...args} />;

export const Default = Template.bind({});
Default.args = {
  tag: 'p', // Default: paragrafo
  color: '#000', // Default: nero
  font: 'Arial, sans-serif', // Default: Arial
  children: 'Questo è un esempio di testo.', // Contenuto di default
};


export const TextSmall = {
  args: {
    tag: 'p',
    color: 'black', 
    size: 'small',
    bold: true,
    children: 'Questo è un paragrafo', 
  }
}
