import React from 'react';
import PropTypes from 'prop-types';
import './skl-ds-divider.css';

export const Divider = ({ color, thickness, orientation, margin }) => {
  const styles = {
    backgroundColor: color,
    height: orientation === 'horizontal' ? thickness : '100%',
    width: orientation === 'vertical' ? thickness : '100%',
    margin: orientation === 'horizontal' ? `${margin} 0` : `0 ${margin}`,
  };

  return <div style={styles} role="separator" />;
};

Divider.propTypes = {
  color: PropTypes.string, // Colore della linea
  thickness: PropTypes.string, // Spessore della linea
  orientation: PropTypes.oneOf(['horizontal', 'vertical']), // Orientamento del divider
  margin: PropTypes.string, // Margine attorno al divider
};

Divider.defaultProps = {
  color: '#ccc', // Colore di default
  thickness: '1px', // Spessore di default
  orientation: 'horizontal', // Orientamento di default
  margin: '8px', // Margine di default
};
