import React from 'react';
import PropTypes from 'prop-types';
import './Icon.css';

export const Icon = ({ iconName, color }) => {
  return (
    <i 
      className={`icon-${iconName}`} 
      style={{ color }} 
      aria-hidden="true"
    ></i>
  );
};

Icon.propTypes = {
  iconName: PropTypes.string.isRequired, // Nome dell'icona (senza "icon-")
  color: PropTypes.string, // Colore dell'icona
};

Icon.defaultProps = {
  color: '#000', // Default: Nero
};

export default Icon;
