import React from 'react';
import PropTypes from 'prop-types';

import './../../../../font/icons/style.css';
import './skl-ds-widget.css';

import { Icon } from './../../atoms/skl-ds-icon/skl-ds-icon';
import { Text } from './../../atoms/skl-ds-text/skl-ds-text';

export const Widget = ({ icon, size, orientation, text })=> {


  return <div className={`skl-ds-widget skl-ds-widget--${orientation === 'horizontal' ? 'horizontal' : 'vertical'}`}>
    <Icon iconName={icon}/>
    <Text tag={'p'} children={text}/>
  </div>;
};

Widget.propTypes = {
  icon: PropTypes.oneOf(['alarm', 'chat', 'check', 'child_care', 'close', 'favorite', 'handyman']).isRequired,
  orientation: PropTypes.oneOf(['horizontal', 'vertical']),
  size: PropTypes.oneOf(['small', 'medium']),
  // color: PropTypes.oneOf(['white', 'black']),
  text: PropTypes.node.isRequired,
};

Widget.defaultProps = {
  icon: 'alert',
  orientation: 'horizontal',
  text: '00'
};
