import { Widget } from './skl-ds-widget';

export default {
  title: 'Example/Molecules/skl-ds-widget',
  component: Widget,
  argTypes: {
    icon: {
      control: {
        type: 'select', 
        options: ['alarm', 'chat', 'check', 'child_care', 'close', 'favorite', 'handyman']
      },
      description: 'Nome dell\'icona (senza prefisso "icon-")',
    },
    size: {
      control: { 
        type: 'radio',
        options: ['small', 'medium', 'large'], 
      },
      description: 'Dimensione dell\'icona',
    },
    text: {
      control: 'text',
      description: 'Contenuto del testo',
    },
  },
};


export const Horizontal = {
  args: {
    icon: 'alert',
    size: 'small',
    orientation: 'horizontal',
    text: '00'
  }
}

export const Vertical = {
  args: {
    icon: 'alert',
    size: 'small',
    orientation: 'vertical',
    text: '00'
  }
}
